SELECT LAST_NAME, DEPARTMENT_NAME, SALARY FROM EMPLOYEES e JOIN DEPARTMENTS d ON e.DEPARTMENT_ID = d.DEPARTMENT_ID WHERE EXISTS (SELECT 'X' FROM EMPLOYEES WHERE SALARY = e.SALARY AND NVL(COMMISSION_PCT, 0) = NVL(e.COMMISSION_PCT, 0) AND DEPARTMENT_ID IN (SELECT DEPARTMENT_ID FROM DEPARTMENTS WHERE LOCATION_ID = 1700));